--Report 'Unvollstndige Produkte'
VALUES (5, 'ReportUnvollstaendigeProdukte', 369188, 'SELECT art.id, art.productNo, lft.text, art.status1, art.status2, art.status3, art.status4, art.creationDate 
FROM Article art, ArticleAV aav, LocaleFreetext lft, NodeArticleRelation nar 
WHERE aav.article.id = art.id 
AND art.id = nar.article.id 
AND aav.attribute.id = 2 
AND lft.textId = aav.freetext 
AND lft.locale.id = ?2 
AND art.articleType.id in (1, 10, 11) 
AND ((art.status1 <> 2) OR (art.status2 <> 2) OR (art.status3 <> 2) OR (art.status4 <> 2)) 
AND #LIST_PARAM#nar.node.id in (?1)#LIST_PARAM#', 369189, 'PurchaseCategory, CurrentLocale', 369198, 'String, String, State, State, State, State, Date', 369200, 1, sysdate, sysdate, 1, 1, 0);

--Report 'Produkte ohne Hauptbild'
VALUES (6, 'ReportProdukteOhneHauptbild', 369190, 'select art.id, art.productNo, lft.text, art.status1, art.status2, art.status3, art.status4, art.creationDate 
from #SCHEMA#.article art 
join #SCHEMA#.article_av aav 
on aav.article = art.id 
join #SCHEMA#.locale_freetext lft 
on lft.textId = aav.freetext 
inner join #SCHEMA#.node_article_rel nar 
on art.id = nar.article 
where aav.attribute = 2 and lft.locale = ?2 and art.articletype in (1, 10, 11) 
and not exists (select article from #SCHEMA#.article_asset_rel where type = 132 and article = art.id and dimension is null) and #LIST_PARAM#nar.node in (?1)#LIST_PARAM#
order by 1, 2, 3',
369191, 'PurchaseCategory, CurrentLocale', 369199, 'String, String, State, State, State, State, Date', 369201, 1, sysdate, sysdate, 1, 1, 1);

--Report 'Produkte ohne Vertriebskanal'
VALUES (7, 'ReportProdukteOhneVertriebskanal', 369192, 'select art.id, art.productNo, lft.text, art.validuntil, art.status1, art.status2, art.status3, art.status4, art.creationDate 
from #SCHEMA#.article art 
inner join #SCHEMA#.article_av aav 
on aav.article = art.id 
inner join #SCHEMA#.locale_freetext lft 
on lft.textid = aav.freetext 
inner join #SCHEMA#.node_article_rel nar 
on nar.article = art.id 
where art.articletype = 1 and (art.validuntil is null or art.validuntil between trunc(sysdate) and ?1) and aav.attribute = 2 and lft.locale = ?3 and #LIST_PARAM#nar.node in (?2)#LIST_PARAM# 
and not exists (select article from #SCHEMA#.node_article_rel nodeartrel 
inner join #SCHEMA#.category_node catnode on nodeartrel.node = catnode.id 
inner join #SCHEMA#.category_node catnode2 on catnode.rootnode = catnode2.id 
inner join #SCHEMA#.version v on v.rootnode = catnode2.id 
inner join #SCHEMA#.publication p on p.id = v.publication 
where p.channeltype = 68 and nodeartrel.article = art.id and nodeartrel.dimension is null)',
369193, 'Date, PurchaseCategory, CurrentLocale', 369194, '1#=#Date', 'String, String, Date, State, State, State, State, Date', 369202, 1, sysdate, sysdate, 1, 1, 1);

--Report 'Genderte Produkte'
VALUES (8, 'ReportGeaenderteProdukte', 369195, 'select distinct art.id, art.productNo, lft.text, trunc(al.datemodified), nvl(concat(us.firstname, us.lastname), us.nickname), art.status1, art.status2, art.status3, art.status4, art.creationDate 
from #SCHEMA#.article art 
inner join #SCHEMA#.article_av aav on aav.article = art.id 
inner join #SCHEMA#.locale_freetext lft on lft.textid = aav.freetext 
inner join #SCHEMA#.audit_log al on art.id = al.article or art.id = al.parentarticle 
inner join #SCHEMA#.users us on al.updateuser = us.id 
inner join #SCHEMA#.node_article_rel nar on art.id = nar.article 
where art.articletype in (1, 10, 11) and aav.attribute = 2 and lft.locale = ?5 and al.article = art.id and al.type in (''insert'',''update'') and al.datemodified between ?1 and ?2 
and (al.updateuser = case ?3 when -1 then al.updateuser else ?3 end) and #LIST_PARAM#nar.node in (?4)#LIST_PARAM#',
369196, 'Date, Date, User, PurchaseCategory, CurrentLocale', 369197, '2#=#Date, 3#=#User', 'String, String, Date, String, State, State, State, State, Date', 369203, 1, sysdate, sysdate, 1, 1, 1);

