--Report 'Nicht lokalisierte Attributtexte'
decode(art.articletype, 2, ''Artikel'', ''Produkt'') as Ebene,
(select l.text from #SCHEMA#.locale_lookuptext l where l.textid = atr.description and l.locale = ?2) as Attribut,
